def how_many_journalists # 1
    puts "1. Il y a #{journa_list.length} 381 comptes de journalistes dans celle liste." # il y a x "handle" (=txt =sections) dans "l'array" (=liste =conteneur)
end

def small_journalist # 2
    puts "2. Le plus petit pseudo de la liste est le suivant : #{journa_list.sort_by(&:length)[0]}." 
    # Traduction de la formule : 1 -->  invoque liste 2 --> trie liste 3 --> trie là par 4 --> longueur 5 --> et donne moi la plus petite valeur de longueur
end

def five_letters_users # 3
    five_users_calc = journa_list.count{|word| word.length == 6} # quand je dirais five_users_calc, je veux que ça m'affiche le résultat de ça
    # on crée des petites boites pour stocker les données avec word entre les barres
    # on demande à ce ces boîtes se remplissent avec les valeurs dont la longueur est = à 6  (pour ignorer le arobase)
    puts "Il y a #{five_users_calc} qui ont un pseudonyme de 5 lettres."
end

def handle_size_sort # 6
    puts "Voici les pseudos en partant du plus court pour arriver au plus long : #{journa_list.sort_by(&:length)}." #marche aussi avec size
end

def alpha_sort # 5
    puts "Voici les pseudos dans l'ordre alphabétique : #{journa_list.sort_by(&:downcase)}" # downcase inclut les minuscules dans le tri alphabétique
end

def user_position # 7
    found_user = journa_list.index("@epenser")
    puts "@epenser se trouve en position : #{found_user}."
end

def list_by_size
    (0..33).each do |i| # la taille minimum sera 0 et la maximale sera 33
    liste = journa_list.count{|word| word.length == i} # liste = prends journa_list et compte {word = autant de fois qu'il y a de longueurs de mots possible}
        if (liste != 0) # != if different from 0, displayed with puts, if not, not displayed 
            puts "Nombre de peuso avec #{i} caractères = #{liste}" #affiche moi les differents options de caracteres avec le nombre de hashs correspondants
        end
    end
end

def perform
    list_by_size
end

perform